import React from "react"
import io from "socket.io-client"
import {Button, TextField, ExpansionPanel, ExpansionPanelSummary, Typography, ExpansionPanelDetails} from '@material-ui/core'
// import axios from 'axios'
// import ExpandMoreIcon from '@material-ui/icons/ExpandMoreIcon';

class Chat extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            id: '',
            username: '',
            message: '',
            messages: []
        };

        this.socket = io('localhost:8080');

        this.socket.on('RECEIVE_MESSAGE', function(data){
            addMessage(data);
        });

        const addMessage = data => {
            console.log(data);
            this.setState({messages: [...this.state.messages, data]});
            console.log(this.state.messages);
        };

        this.sendMessage = ev => {
            ev.preventDefault();
            this.socket.emit('SEND_MESSAGE', {
                author: this.state.username,
                message: this.state.message
            })
            this.setState({message: ''});
            console.log(this.state.clients)
        }
    }

    componentDidMount() {
        console.log(io)
        this.socket.on('connect', ()=> {
            this.setState({
                id: this.socket.id
            })
        })
        // axios.get('/')
        //     .then(function (response) {
        //         console.log(response);
        //     })
        //     .catch(function (error) {
        //         console.log(error);
        //     });
        fetch('/', {
            headers : { 
              'Content-Type': 'application/json',
              'Accept': 'application/json'
             }

          })
          .then((response) => response.json())
          .then((messages) => {console.log("messages");});
    }

    render(){
        return (
            <div className="container">
                <div className="row">
                    <div className="col-4">
                        <div className="card">
                            <div className="card-body">
                                <div className="card-title">Global Chat</div>
                                <hr/>
                                <div className="messages">
                                    {this.state.messages.map(message => {
                                        return (
                                            <div>{message.author}: {message.message}</div>
                                        )
                                    })}
                                </div>
                            </div>
                            <ExpansionPanel>
                                <ExpansionPanelSummary>
                                    <Typography>{this.state.id}</Typography>
                                </ExpansionPanelSummary>
                                <ExpansionPanelDetails>
                                <Typography>
                                    <div className="card-footer">
                                        <TextField 
                                            id="outlined-bare" 
                                            margin="normal" 
                                            variant="outlined" 
                                            placeholder="Username" 
                                            value={this.state.username} 
                                            onChange={ev => this.setState({username: ev.target.value})} />
                                        <TextField 
                                            id="outlined-bare" 
                                            margin="normal" 
                                            variant="outlined" 
                                            placeholder="Message" 
                                            value={this.state.message} 
                                            onChange={ev => this.setState({message: ev.target.value})}/>
                                        <br/>
                                        <Button onClick={this.sendMessage} variant="outlined" color="primary">Send</Button>
                                    </div>
                                </Typography>
                                </ExpansionPanelDetails>
                            </ExpansionPanel>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Chat;
