import React from "react"
import io from "socket.io-client"
import {Paper, Button, TextField, ExpansionPanel, ExpansionPanelSummary, Typography, ExpansionPanelDetails} from '@material-ui/core'
import axios from 'axios'
// import ExpandMoreIcon from '@material-ui/icons/ExpandMoreIcon';

import './Chat.css'

class Chat extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            render: 1,
            clients: [],
            id:'',
            username: 'Lukas',
            message: '',
            messages: []
        };

        this.socket = io('localhost:8080');

        this.socket.on('RECEIVE_MESSAGE', function(data){
            addMessage(data);
        });

        this.socket.on('client', function(data) {
            addId(data);
            addAllClients()
        })

        this.socket.on('allClients', function(data) {
            addAllClients(data)
        })


        const addMessage = data => {
            console.log(data);
            this.setState({messages: [...this.state.messages, data]});
            console.log(this.state.messages);
        };

        const addId = data => {
            this.setState({id: data})
        }

        const addAllClients = data => {
            console.log(data)
            this.setState({clients: [data]})
            // this.state.clients.push(data)
        }

        this.sendMessage = ev => {
            ev.preventDefault();
            this.socket.emit('SEND_MESSAGE', {
                author: this.state.id,
                message: this.state.message
            })
            this.setState({message: ''});
        }
    }

    render() {
        return (      
            <div>
                {this.state.clients.map((client, i) => (
                    <ul key={i}>
                        <li>{client}</li>
                    </ul>
                ))}
            {/* <Paper>
                {this.state.clients.map((client, i) => (       
                    <div key={i} className='chat-container' onClick={(e)=> {console.log(e.target)}}>
