import React from "react"
import io from "socket.io-client"
import {Paper, Button, TextField, ExpansionPanel, ExpansionPanelSummary, Typography, ExpansionPanelDetails} from '@material-ui/core'
import axios from 'axios'
// import ExpandMoreIcon from '@material-ui/icons/ExpandMoreIcon';

import './Chat.css'

class Chat extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            id:'',
            clients: [],
            username: 'Lukas',
            message: '',
            messages: []
        };

        this.socket = io('localhost:8080');

        this.socket.on('RECEIVE_MESSAGE', function(data){
            addMessage(data);
        });

        this.socket.on('client', function(data) {
            addId(data);
        })

        this.socket.on('allClients', function(data) {
            addAllClients(data)
        })


        const addMessage = data => {
            console.log(data);
            this.setState({messages: [...this.state.messages, data]});
            console.log(this.state.messages);
        };

        const addId = data => {
            this.setState({id: data})
        }

        const addAllClients = data => {
            console.log(data)
            this.setState({clients: [...this.state.clients, data]})
        }

        this.sendMessage = ev => {
            ev.preventDefault();
            this.socket.emit('SEND_MESSAGE', {
                author: this.state.id,
                message: this.state.message
            })
            this.setState({message: ''});
        }

        // this.privateMsg = this.privateMsg.bind(this)
    }

    // componentWillMount() {
    //     this.fetchData()
    //     console.log(this.state.clients)
    // }

    // fetchData() {
    //     var that = this;
    //     axios.get('/clients')
    //         .then(function (response) {
    //             console.log(response.data)
    //             response.data.map(data => 
    //                 that.state.clients.push(data)
    //             )
    //             // that.state.clients.push(response.data)
    //             // that.setState({clients: [...that.state.clients, response.data]})
    //         })
    //         .catch(function (error) {
    //             console.log(error);
    //         });
    // }

    // privateMsg(e) {
    //     // this.state.clients.map((client)=>{
    //     //     if(client) {

    //     //     }
    //     // })
    //     // console.log(e.target.getAttribute('tabindex'))
    // }

    render() {
        return (

            <div>
            <Paper>
                {this.state.clients.map((client,i) => (
                    <div key={i} className='chat-container' onClick={()=> {console.log(this.state.id)}}>
                    <ExpansionPanel>
                        <ExpansionPanelSummary>
                        <Typography>{client}</Typography>
                        </ExpansionPanelSummary>
                        <ExpansionPanelDetails>
                            <Typography>
                                <div className="messages">
                                    {this.state.messages.map(message => {
                                        return (
                                            <div>{message.author}: {message.message}</div>
                                        )
                                    })}
                                </div>
                                <div className="card-footer">
                                    <TextField 
                                        id="outlined-bare" 
                                        margin="normal" 
                                        variant="outlined" 
                                        placeholder="Username" 
                                        value={this.state.username} 
                                        onChange={ev => this.setState({username: ev.target.value})} />
                                    <TextField 
                                        id="outlined-bare" 
                                        margin="normal" 
                                        variant="outlined" 
                                        placeholder="Message" 
                                        value={this.state.message} 
                                        onChange={ev => this.setState({message: ev.target.value})}/>
                                    <br/>
                                <Button onClick={this.sendMessage} variant="outlined" color="primary">Send</Button>
                                </div>
                        </Typography>
                    </ExpansionPanelDetails>
                  </ExpansionPanel>
                  </div>
                ))}
            </Paper>
            </div>
        );
    }
}

export default Chat;
